mapper.insert:

insert into itms_product (id, product_name, product_desc,
is_deleted, create_time, create_by,
update_time, update_by, commons
)
values (#{id,jdbcType=BIGINT}, #{productName,jdbcType=VARCHAR}, #{productDesc,jdbcType=VARCHAR},
'N', NOW(), #{createBy,jdbcType=VARCHAR},
NOW(), #{updateBy,jdbcType=VARCHAR}, #{commons,jdbcType=VARCHAR}
)

mapper.select:
select count(*) from itms_product
where product_name = #{productName}

    and id!=#{id}

and is_deleted = 'N'
