mapper.select:

SELECT ip.*,ipd.product_name productName FROM itms_project ip
INNER JOIN itms_product ipd ON ip.product_id = ipd.id AND ipd.is_deleted = 'N'
WHERE ip.is_deleted = 'N' AND ip.id = #{id}

mapper.insert:

insert into itms_project (id, product_id, project_name,
project_short_name, project_desc, state,
is_deleted, create_time, create_by,
update_time, update_by, commons
)
values (#{id,jdbcType=BIGINT}, #{productId,jdbcType=BIGINT}, #{projectName,jdbcType=VARCHAR},
#{projectShortName,jdbcType=VARCHAR}, #{projectDesc,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR},
'N', NOW(), #{createBy,jdbcType=VARCHAR},
NOW(), #{updateBy,jdbcType=VARCHAR}, #{commons,jdbcType=VARCHAR}
)

mapper.select:
select count(*) from itms_project
WHERE project_name = #{projectName}

    and id!=#{id}

SELECT count(*) FROM itms_project ip
WHERE ip.is_deleted = 'N'
AND ip.product_id = #{id}
