mapper.insert:

insert into itms_project_version (id, project_id, version_name,
publish_date, version_desc, state,
is_deleted, create_time, create_by,
update_time, update_by, commons
)
values (#{id,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{versionName,jdbcType=VARCHAR},
#{publishDate,jdbcType=DATE}, #{versionDesc,jdbcType=VARCHAR}, '未开始',
'N', NOW(), #{createBy,jdbcType=VARCHAR},
NOW(), #{updateBy,jdbcType=VARCHAR}, #{commons,jdbcType=VARCHAR}
)

mapper.select:
select count(*) from itms_project_version 
where version_name = #{versionName} and is_deleted = 'N'
and project_id = #{projectId,jdbcType=BIGINT}

    AND id != #{id}
